import React, {useRef, useState} from 'react'
import Button from 'react-bootstrap/Button'
import { useHistory } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './Director.css'
import { signup } from '../firebase';

const Director = () => {
    const [loading, setLoading] = useState(false);

    const emailRef = useRef();
    const passwordRef = useRef();

let History  = useHistory();

async function handleSignup () {

    setLoading(true);
    try {
        
        await signup(emailRef.current.value,passwordRef.current.value);
    } catch {
        alert("Error, cuenta ya registrada")
    }
    setLoading(false);
}

    return (
        <div>
            <Button onClick = {()=>History.push('/home')}>Inicio</Button>
            <br/>
            <br/>
            <Button onClick = {()=>History.push('/Roles')}> Regresar</Button>

            <form>
  <label>
    Correo:
    <input ref={emailRef} type="text" name="name" />
    Password:
    <input ref={passwordRef} type="password" name="password" />
  </label>
  <Button disable= {loading} onClick = {handleSignup}>Registrar</Button>
</form>
        </div>
    )
}

export default Director
