import React, {useState, useRef} from 'react';
import {Formulario,  ContenedorBotonCentrado, Boton, MensajeExito, MensajeError, } from './Elementos/Formularios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faExclamationTriangle } from '@fortawesome/free-solid-svg-icons';
import Input from './ComponentesInicio/Input';
import './InicioS.css'
import logo from '../Inicio_sesion/Logo/logo.svg'
import { useHistory } from "react-router-dom";
import {Login, useAuth, Logout} from "../firebase"

const App = () => {
	const [usuario, cambiarUsuario] = useState({campo: '', valido: null});
	const [password, cambiarPassword] = useState({campo: '', valido: null});
	const [formularioValido, cambiarFormularioValido] = useState(null);
	const [loading, setLoading] = useState(false);
	let history = useHistory();

	const emailRef = useRef();
    const passwordRef = useRef();
	const currentUser = useAuth();

	async function handleLogin () {

		setLoading(true);
		try {
			
			await Login(emailRef.current.value,passwordRef.current.value);
		} catch {
			alert("Error, cuenta no registrada")
		}
		setLoading(false);
	}

	async function handleLogout () {
		setLoading(true)
		try {
		await Logout();
		} catch {
			alert("Error");
		}
		setLoading(false)
	}
	

	return (		
		<div className = "Fondo">
		<main className="main">
			<div className = "cont">
			<img className = "Logo"
                                alt=""
                                src={logo}
                                width="400"
                                height="150"
                            />
							<br/>
							<div className = "">
			<Formulario action="" >
				<Input
					tipo="text"
					label="Usuario"
					placeholder="john123"
					name="usuario"
					ref={emailRef}
					className=""
					leyendaError="El usuario tiene que ser de 4 a 16 dígitos y solo puede contener numeros, letras y guion bajo."
					
				/>
				
				<Input
					tipo="password"
					label="Contraseña"
					name="password1"
					ref={passwordRef}
					className=""
					leyendaError="La contraseña tiene que ser de 4 a 12 dígitos."
					
				/>
				
				{formularioValido === false && <MensajeError>
					<p>
						<FontAwesomeIcon icon={faExclamationTriangle}/>
						<b>Error:</b> Por favor rellena el formulario correctamente.
					</p>
				</MensajeError>}
				<br/>
				<ContenedorBotonCentrado>
					<Boton type="submit" className = "Botonsito" disable= {loading || currentUser} onClick={handleLogin}><span>Ingresar</span></Boton>
					<Boton type="submit" className = "Botonsito" disable= {loading || currentUser} onClick={handleLogout}><span>salir</span></Boton>
					{/* {formularioValido === true && <MensajeExito>Formulario enviado exitosamente!</MensajeExito>} */}
				</ContenedorBotonCentrado>
			</Formulario>
			</div>
			</div>
		</main>
		</div>
	);
}
 
export default App;
